mapper.select:
/* GdbMapper.selectEdgeList */
SELECT DISTINCT ta.e_label, ca.count FROM (
) AS ta LEFT OUTER JOIN ( MATCH (n1:${category[0]})-[edge]-(b)-[edge1]-(n2:${category[0]}) WHERE
AND
AND id(b) IN ( SELECT id(b0) FROM
WHERE
) RETURN label(edge) as e_label, COUNT(DISTINCT b) ) AS ca ON ta.e_label = ca.e_label
MATCH (n1) - [edge] - (n2) WHERE
RETURN label(edge) as e_label, COUNT(DISTINCT id(n2)) as count ORDER BY count DESC

mapper.insert:
        /* GdbMapper.insertAgConvertMeta */ 
        INSERT  INTO public.ag_convert_meta(
                graph
	         ,  lab_kind
             ,  lab_nm)
	    VALUES (#{graph}
             ,  #{labKind}
             ,  #{labNm})

mapper.delete:
        /* GdbMapper.deleteMetaVertex */ 
        DELETE  FROM public.ag_convert_meta
	     WHERE  graph  = #{graph}
           AND  lab_nm = #{labNm}
