mapper.insert:
        /* VertexMapper.insertMetaVertex */ 
        INSERT  INTO public.ag_meta_vertex(
                graph
	         ,  vertex
             ,  col_sn
             ,  col_nm
             ,  col_tp
             ,  key_tp
             ,  col_comment)
	    VALUES (#{graph}
             ,  #{tableNm}
             ,  #{ordinalPosition}
             ,  #{columnName}
             ,  #{columnType}
             ,  #{columnKey}
             ,  #{columnComment})

mapper.delete:
        /* VertexMapper.deleteMetaVertex */ 
        DELETE  FROM public.ag_meta_vertex
	     WHERE  graph  = #{graph}
           AND  vertex = #{tableNm}
